SELECT 
    so.order_nr,
    soi.bob_id_sales_order_item,
    soi.sku,
    catree.lvl1,
    sup.id_supplier,
    sel.short_code,
    sup.name,
    sup.type,
    sel.tax_class,
    IFNULL(poi.cost, sp.cost) 'cogp',
    CASE
        WHEN sup.type = 'supplier' THEN NULL
        WHEN sel.tax_class = 'international' THEN NULL
        WHEN FIND_IN_SET(sup.id_supplier, @vip) THEN 'vip'
        WHEN FIND_IN_SET(sup.id_supplier, @rsp) THEN 'rsp'
    END 'campaign',
    CASE
        WHEN
            cty.name LIKE '%Jakarta%'
                OR cty.name LIKE '%Bogor%'
                OR cty.name LIKE '%Depok%'
                OR cty.name LIKE '%Tangerang%'
                OR cty.name LIKE '%Bekasi%'
        THEN
            'jabodetabek'
        WHEN FIND_IN_SET(soa.fk_customer_address_region, @free) THEN 'other_free_cities'
        ELSE 'paid'
    END 'zone_type'
FROM
    oms_live.ims_sales_order so
        LEFT JOIN
    oms_live.ims_sales_order_item soi ON so.id_sales_order = soi.fk_sales_order
        LEFT JOIN
    oms_live.ims_sales_order_address soa ON so.fk_sales_order_address_shipping = soa.id_sales_order_address
        LEFT JOIN
    oms_live.ims_customer_address_region dst ON soa.fk_customer_address_region = dst.id_customer_address_region
        LEFT JOIN
    oms_live.ims_customer_address_region cty ON dst.fk_customer_address_region = cty.id_customer_address_region
        LEFT JOIN
    oms_live.wms_inventory inv ON soi.id_sales_order_item = inv.sales_order_item_id
        LEFT JOIN
    oms_live.ims_purchase_order_item poi ON inv.fk_purchase_order_item = poi.id_purchase_order_item
        LEFT JOIN
    oms_live.ims_supplier_product sp ON soi.sku = sp.sku
        LEFT JOIN
    bob_live.catalog_simple cs ON soi.sku = cs.sku
        LEFT JOIN
    bob_live.catalog_config cc ON cs.fk_catalog_config = cc.id_catalog_config
        LEFT JOIN
    (SELECT 
        IFNULL(lvl0_id, '') 'lvl0_id',
            IFNULL(lvl0, '') 'lvl0',
            IFNULL(lvl1_id, '') 'lvl1_id',
            IFNULL(lvl1, '') 'lvl1',
            IFNULL(lvl2_id, '') 'lvl2_id',
            IFNULL(lvl2, '') 'lvl2',
            IFNULL(lvl3_id, '') 'lvl3_id',
            IFNULL(lvl3, '') 'lvl3',
            IFNULL(lvl4_id, '') 'lvl4_id',
            IFNULL(lvl4, '') 'lvl4',
            IFNULL(lvl5_id, '') 'lvl5_id',
            IFNULL(lvl5, '') 'lvl5',
            IFNULL(lvl6_id, '') 'lvl6_id',
            IFNULL(lvl6, '') 'lvl6',
            cc0_id,
            cc0_name
    FROM
        (SELECT 
        MIN(IF(catree.lvl = 0, catree.cc1_name, NULL)) 'lvl0',
            MIN(IF(catree.lvl = 0, catree.cc1_id, NULL)) 'lvl0_id',
            MIN(IF(catree.lvl = 1, catree.cc1_name, NULL)) 'lvl1',
            MIN(IF(catree.lvl = 1, catree.cc1_id, NULL)) 'lvl1_id',
            MIN(IF(catree.lvl = 2, catree.cc1_name, NULL)) 'lvl2',
            MIN(IF(catree.lvl = 2, catree.cc1_id, NULL)) 'lvl2_id',
            MIN(IF(catree.lvl = 3, catree.cc1_name, NULL)) 'lvl3',
            MIN(IF(catree.lvl = 3, catree.cc1_id, NULL)) 'lvl3_id',
            MIN(IF(catree.lvl = 4, catree.cc1_name, NULL)) 'lvl4',
            MIN(IF(catree.lvl = 4, catree.cc1_id, NULL)) 'lvl4_id',
            MIN(IF(catree.lvl = 5, catree.cc1_name, NULL)) 'lvl5',
            MIN(IF(catree.lvl = 5, catree.cc1_id, NULL)) 'lvl5_id',
            MIN(IF(catree.lvl = 6, catree.cc1_name, NULL)) 'lvl6',
            MIN(IF(catree.lvl = 6, catree.cc1_id, NULL)) 'lvl6_id',
            cc0_id,
            cc0_name
    FROM
        (SELECT 
        *, IF(cc1_id = 1, @lvl:=0, @lvl:=@lvl + 1) 'lvl'
    FROM
        (SELECT 
        cc0.id_catalog_category 'cc0_id',
            cc0.name_en 'cc0_name',
            cc1.id_catalog_category 'cc1_id',
            cc1.name_en 'cc1_name',
            cc1.lft,
            cc1.rgt
    FROM
        bob_live.catalog_category cc0
    LEFT JOIN bob_live.catalog_category cc1 ON cc0.lft >= cc1.lft
        AND cc0.rgt <= cc1.rgt
    WHERE
        cc0.status = 'active'
    ORDER BY cc0.id_catalog_category , cc1.lft , cc1.rgt
    LIMIT 1000000000) catree) catree
    GROUP BY catree.cc0_id) result) catree ON cc.primary_category = catree.cc0_id
        LEFT JOIN
    bob_live.supplier sup ON soi.bob_id_supplier = sup.id_supplier
        LEFT JOIN
    screport.seller sel ON sup.id_supplier = sel.src_id
