SET @extractend = '2017-08-15';-- This MUST be D + 1

CREATE TEMPORARY TABLE period_cat (
   `period` DATETIME NOT NULL,
   KEY (period)
);

delimiter //
CREATE PROCEDURE load_date_cat()
BEGIN
	SET @start_loop_cat = @extractstart;
	WHILE @start_loop_cat < @extractend DO
		INSERT INTO period_cat VALUES (@start_loop_cat);
        SET @start_loop_cat = DATE_ADD(@start_loop_cat, INTERVAL 1 DAY);
	END WHILE;
END //
