                        THEN
                            'CA: Accessories'
                        ELSE 'CA'
                    END
                WHEN
                    level1 = 'Mobiles & Tablets'
                THEN
                    CASE
                        WHEN level2 = 'Accessories' THEN 'MT: Accessories'
                        ELSE 'MT'
                    END
                WHEN
                    level1 = 'TV, Audio / Video, Gaming & Wearables'
                THEN
                    CASE
                        WHEN
                            level2 = 'Audio'
                        THEN
                            CASE
                                WHEN level3 = 'Headphones & Headsets' THEN 'TAGW: Accessories'
                                WHEN
                                    level3 = 'Portable Players'
                                        AND level4 = 'Portable Audio Accessories'
                                THEN
                                    'TAGW: Accessories'
                                WHEN
                                    level3 = 'Live Sound & Stage'
                                        AND (level4 = 'Microphone Accessories'
                                        OR level4 = 'Live, Sound & Stage Accessories')
                                THEN
                                    'TAGW: Accessories'
                                WHEN
                                    level3 = 'Home Entertainment'
                                        AND level4 = 'Home Audio Accessories'
                                THEN
                                    'TAGW: Accessories'
                                WHEN level3 = 'Portable Speakers' THEN 'TAGW: Accessories'
                                ELSE 'TAGW'
                            END
                        WHEN
                            level2 = 'Video'
                                AND (level3 = 'Media Players'
                                OR level3 = 'Projector Accessories')
                        THEN
                            'TAGW: Accessories'
                        WHEN
                            level2 = 'Wearable Technology'
                                OR level2 = 'TV Accessories'
                                OR level2 = 'App-enabled Accessories'
                        THEN
                            'TAGW: Accessories'
                        ELSE 'TAGW'
                    END
                WHEN
                    level1 = 'Baby & Toddler'
                        OR level1 = 'Bedding & Bath'
                        OR level1 LIKE '%Furniture%'
                        OR level1 = 'Kitchen & Dining'
                        OR level1 = 'Laundry & Cleaning'
                        OR level1 = 'Stationery & Craft'
                        OR level1 = 'Tools, DIY & Outdoor'
                        OR level1 = 'Travel & Luggage'
                        OR level1 = 'Groceries'
                        OR level1 = 'Health & Beauty'
                        OR level1 = 'Home & Living'
                        OR level1 = 'Home Appliances'
                        OR level1 = 'Media, Music & Books'
                        OR level1 = 'Motors'
                        OR level1 = 'Pet Supplies'
                        OR level1 = 'Sports & Outdoors'
                        OR level1 = 'Toys & Games'
                        OR level1 = 'Vouchers and Services'
                        OR level1 = 'Watches Sunglasses Jewellery'
                THEN
                    level1
                ELSE 'Other'
            END 'category',
            CASE
                WHEN ac.campaign LIKE '%VIP%' THEN 'VIP'
                ELSE 'NON VIP'
            END 'temp_campaign',
            CASE
                WHEN
                    ac.delivered_date >= @extractstart
                        AND ac.delivered_date < @extractend
                THEN
                    DATE(ac.delivered_date)
                ELSE 0
            END 'delivered_at',
            CASE
                WHEN
                    ac.not_delivered_date >= @extractstart
                        AND ac.not_delivered_date < @extractend
                THEN
                    DATE(ac.not_delivered_date)
                ELSE 0
            END 'failed_at',
            CASE
                WHEN
                    ac.first_shipped_date >= @extractstart
                        AND ac.first_shipped_date < @extractend
                THEN
                    DATE(ac.first_shipped_date)
                ELSE 0
            END 'shipped_at',
            CASE
                WHEN
                    ac.order_date >= @extractstart
                        AND ac.order_date < @extractend
                THEN
                    DATE(ac.order_date)
                ELSE 0
            END 'created_at',
            IFNULL(ac.paid_price / 1.1, 0) + IFNULL(ac.shipping_surcharge / 1.1, 0) + IFNULL(ac.shipping_amount / 1.1, 0) + IF(ac.coupon_type <> 'coupon', IFNULL(ac.coupon_money_value / 1.1, 0), 0) 'nmv',
            ac.delivery_cost_item + ac.delivery_cost_discount_item + ac.delivery_cost_vat_item + ac.insurance_3pl_item + ac.insurance_vat_3pl_item 'delivery_cost',
            ac.pickup_cost_item + ac.pickup_cost_discount_item + ac.pickup_cost_vat_item 'pickup_cost'
    FROM
        anondb_calculate ac
    LEFT JOIN category_tree ct ON ac.primary_category = ct.id_primary_category
    WHERE
        (delivered_date >= @extractstart
            AND delivered_date < @extractend)
            OR (not_delivered_date >= @extractstart
            AND not_delivered_date < @extractend)
            OR (first_shipped_date >= @extractstart
            AND first_shipped_date < @extractend)
            OR (order_date >= @extractstart
            AND order_date < @extractend)
    HAVING pass = 1) result
    GROUP BY category , created_at , shipped_at , delivered_at , failed_at) result
    LEFT JOIN period_cat pd ON (pd.period = result.created_at
        OR pd.period = result.shipped_at
        OR pd.period = result.delivered_at
        OR pd.period = result.failed_at)
    GROUP BY pd.period) result;

